	public function getPHPCode($sContent)
	{
		$sPHPCode = "";
		preg_match('/(\<\?=|\<\?php=|\<\?php)(.*?)\?\>/is',$sContent, $aMatches);
		if(isset($aMatches[1]))
		{
			$sPHPCode = $aMatches[1];
		}
		return $sPHPCode;
	}
    public static function getKeywords($string, $remove = array(), $to_string = true)
    {
        if (empty($string))
        {
            return $to_string ? "" : array();
        }
        if (count($remove) <= 0)
        {
            $remove = self::$_removeString;
        }
        $string = strip_tags($string);
        $aWords = explode(' ', $string);
        if ($to_string)
        {
            $keywords = '';
        } else
        {
            $keywords = array();
        }
        foreach ($aWords as $sWord)
        {
            if (empty($sWord) || in_array(strtolower($sWord), $remove))
            {
                continue;
            }

            if (strlen($sWord) < 2)
            {
                continue;
            }
            if ($to_string)
            {
                $keywords .= $sWord . ',';
            } else
            {
                $keywords[] = $sWord;
            }
        }
        if ($to_string)
        {
            $keywords = rtrim($keywords, ',');
            return $keywords;
        }
        return $keywords;
    }

